// Definition for a binary tree node.
class TreeNode
{
    public $val = null;
    public $left = null;
    public $right = null;

    function __construct($val = 0, $left = null, $right = null)
    {
        $this->val = $val;
        $this->left = $left;
        $this->right = $right;
    }
}

class Solution
{
    /**
     * @param TreeNode $root
     * @return Integer
     */
    function widthOfBinaryTree(TreeNode $root): int
    {
        $minAndMaxScoresPerLevel = [];

        $queue = [$root];
        $levels = [1];
        $scores = [1];

        while (!empty($queue)) {
            $node = array_shift($queue);
            $level = array_shift($levels);
            $score = array_shift($scores);

            $minAndMaxScoresPerLevel[$level]['min'] = isset($minAndMaxScoresPerLevel[$level]) ? min($score, $minAndMaxScoresPerLevel[$level]['min']) : $score;
            $minAndMaxScoresPerLevel[$level]['max'] = isset($minAndMaxScoresPerLevel[$level]) ? max($score, $minAndMaxScoresPerLevel[$level]['max']) : $score;

            if ($node->left) {
                $queue[] = $node->left;
                $levels[] = $level + 1;
                $scores[] = $score * 2;
            }

            if ($node->right) {
                $queue[] = $node->right;
                $levels[] = $level + 1;
                $scores[] = $score * 2 + 1;
            }
        }

        $maxWidth = 0;

        foreach ($minAndMaxScoresPerLevel as $item) {
            $maxWidth = max($item['max'] - $item['min'] + 1, $maxWidth);
        }

        return $maxWidth;
    }
}

